import React from "react";
import styled from "styled-components";

export default function Content() {
  return (
    <React.Fragment>
      <div>
        <Button
          onClick={seriesList}
          value={this.state.keyword}
          onChange={this.keywordChanged}
          placeholder="keyword"
        >
          SERIES
        </Button>
        <Button>MOVIES</Button>
      </div>
    </React.Fragment>
  );
}

keywordChanged = e => this.setState({ keyword: e.target.value });

searchMovie = () =>
  fetch(`http://www.omdbapi.com/?apikey=539d07a9&s=${this.state.keyword}`).then(
    response => response.json().then(this.renderMovies)
  );

renderMovies = response => this.setState({ movies: response.Search });

const Button = styled.button`
  font-family: sans-serif;
  font-size: 1.3rem;
  border: none;
  height: 170px;
  width: 140px;
  margin: 5px;
  color: white;
  background: url(/film.jpg)no-repeat center/cover;
  top: 40px;
  top-margin 10px;
`;
